// lib/models/player.dart import 'package:football_hero/models/user_role.dart';  class Player {   final String id;   final double? height;   final double? weight;   final List<String> positions;   final String? strongLeg;   final DateTime createdAt;   final Map<String, dynamic>? skills;    Player({     required this.id,     this.height,     this.weight,     required this.positions,     this.strongLeg,     required this.createdAt,     this.skills,   });    factory Player.fromJson(Map<String, dynamic> json) {     return Player(       id: json['id'],       height: json['height']?.toDouble(),       weight: json['weight']?.toDouble(),       positions: (json['positions'] as List?)?.map((e) => e as String).toList() ?? [],       strongLeg: json['strong_leg'],       createdAt: json['created_at'] != null ?          DateTime.parse(json['created_at']) :          DateTime.now(),       skills: json['skills'],     );   }    Map<String, dynamic> toJson() {     return {       'id': id,       'height': height,       'weight': weight,       'positions': positions,       'strong_leg': strongLeg,       'created_at': createdAt.toIso8601String(),       'skills': skills,     };   } }
