// lib/models/team.dart class Team {   final String id;   final String name;   final DateTime createdAt;   final String? forumId;   final String? logoUrl;   final String? bannerUrl;   final String? status;   final String? createdBy;   final String? approvedBy;   final DateTime? updatedAt;    Team({     required this.id,     required this.name,     required this.createdAt,     this.forumId,     this.logoUrl,     this.bannerUrl,     this.status,     this.createdBy,     this.approvedBy,     this.updatedAt,   });    factory Team.fromJson(Map<String, dynamic> json) {     return Team(       id: json['id'],       name: json['name'],       createdAt: json['created_at'] != null ?          DateTime.parse(json['created_at']) :          DateTime.now(),       forumId: json['forum_id'],       logoUrl: json['logo_url'],       bannerUrl: json['banner_url'],       status: json['status'],       createdBy: json['created_by'],       approvedBy: json['approved_by'],       updatedAt: json['updated_at'] != null ?          DateTime.parse(json['updated_at']) :          null,     );   }    Map<String, dynamic> toJson() {     return {       'id': id,       'name': name,       'created_at': createdAt.toIso8601String(),       'forum_id': forumId,       'logo_url': logoUrl,       'banner_url': bannerUrl,       'status': status,       'created_by': createdBy,       'approved_by': approvedBy,       'updated_at': updatedAt?.toIso8601String(),     };   } }
