 // lib/models/user.dart import 'package:football_hero/models/user_role.dart';  class User {   final String id;   final String name;   final DateTime? dob;   final String? address;   final String? city;   final String? phoneNumber;   final int? age;   final String? profilePictureUrl;   final String? accountStatus;   final UserRole role;   final DateTime? createdAt;    User({     required this.id,     required this.name,     this.dob,     this.address,     this.city,     this.phoneNumber,     this.age,     this.profilePictureUrl,     this.accountStatus,     required this.role,     this.createdAt,   });    factory User.fromJson(Map<String, dynamic> json) {     return User(       id: json['id'],       name: json['name'],       dob: json['dob'] != null ? DateTime.parse(json['dob']) : null,       address: json['address'],       city: json['city'],       phoneNumber: json['phone_number'],       age: json['age'],       profilePictureUrl: json['profile_picture_url'],       accountStatus: json['account_status'],       role: UserRole.fromString(json['role']),       createdAt: json['created_at'] != null ? DateTime.parse(json['created_at']) : null,     );   }    Map<String, dynamic> toJson() {     return {       'id': id,       'name': name,       'dob': dob?.toIso8601String(),       'address': address,       'city': city,       'phone_number': phoneNumber,       'age': age,       'profile_picture_url': profilePictureUrl,       'account_status': accountStatus,       'role': role.name,       'created_at': createdAt?.toIso8601String(),     };   } }
